<style lang="scss">
.header {
  background-color: rgb(28, 129, 176);
  position: relative;
  z-index: 10;
  font-size: 16px;
  will-change: transform;

  &__inner {
    position: relative;
    display: flex;
    height: 46px;
    padding: 16px 40px;
    margin: 0 auto;
    max-width: 1350px;
    align-items: center;
    justify-content: space-between;
  }
}
.logo {
  display: flex;
  flex-direction: column;
  text-decoration: none;
  align-items: flex-start;
  span {
    color: #fff;
    font-size: 0.93rem;
    margin-top: 7px;
  }
}
.cart {
  position: relative;

  &__count {
    position: absolute;
    top: -1px;
    left: 10px;
    margin: 0 auto;
    color: #fff;
    min-width: 12px;
    height: 12px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 12px;
    background-color: #309acb;
    border: 2px solid #fff;
    font-size: 13px;
    font-weight: 600;
    line-height: normal;
    letter-spacing: normal;
    text-align: center;
    padding: 3px;
  }
}
</style>
